    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       conn=MySqlConnect.ConnectDB();
       //String sql="Select * from student_inforomation where S_id=? and Password=?";
      try {
               conn=MySqlConnect.ConnectDB();
            Statement pst=conn.createStatement();
            PreparedStatement update_std=conn.prepareStatement ("insert into employee_info values (?,?,?,?,?,?,?,?)");
            update_std.setString(1,ID.getText());
            update_std.setString(2,First_Name.getText());
            update_std.setString(3,Last_Name.getText());
            update_std.setString(4,Position.getText());
            update_std.setString(5,Mobile.getText());
            update_std.setString(6,buttonGroup1.getSelection().getActionCommand());
            update_std.setString(7,Par_Address.getText());
            update_std.setString(8,Pre_Address.getText());
       
            update_std.executeUpdate();
            JOptionPane.showMessageDialog(null,"Data Added Succcessfully");
        } 
        catch (Exception e) {
             JOptionPane.showMessageDialog(null," unsucccessful");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        conn=MySqlConnect.ConnectDB();
       //String sql="Select * from student_inforomation where S_id=? and Password=?";
      try {
               conn=MySqlConnect.ConnectDB();
            Statement pst=conn.createStatement();
            PreparedStatement update_std=conn.prepareStatement ("DELETE FROM `employee_info` WHERE ID=?");
            update_std.setString(1,ID.getText());           
            update_std.executeUpdate();
            JOptionPane.showMessageDialog(null,"Data deleted Succcessfully");
        } 
        catch (Exception e) {
             JOptionPane.showMessageDialog(null," unsucccessful");
        }
    }//GEN-LAST:event_jButton4ActionPerformed

