    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
       conn=MySqlConnect.ConnectDB();
       //String sql="Select * from student_inforomation where S_id=? and Password=?";
      try {
               conn=MySqlConnect.ConnectDB();
            Statement pst=conn.createStatement();
            PreparedStatement update_std=conn.prepareStatement ("update employee_info SET First_Name=?,Last_Name=?,Position=?,Mobile =?,Gander=?,Parmanenet_Address=?,Parmanenet_Address=? WHERE ID=?");
            update_std.setString(8,ID.getText());
            update_std.setString(1,First_Name.getText());
            update_std.setString(2,Last_Name.getText());
            update_std.setString(3,Position.getText());
            update_std.setString(4,Mobile.getText());
            update_std.setString(5,buttonGroup1.getSelection().getActionCommand());
            update_std.setString(6,Par_Address.getText());
            update_std.setString(7,Pre_Address.getText());
        
           
            update_std.executeUpdate();
            JOptionPane.showMessageDialog(null,"Data Update Succcessfully");
        } 
        catch (Exception e) {
             JOptionPane.showMessageDialog(null," unsucccessful");
        } 
    }//GEN-LAST:event_jButton2ActionPerformed

